import React from "react";

class Fetch extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            comment: ''
        }
    }

    fetchData = () => {
        this.setState((state) => {
            comment: fetch(' https://jsonplaceholder.typicode.com/comments')
                        .then(data => data.json()[0])
        })
    }

    render() {
        return(
            <p>{this.fetchData()}</p>
        )
    }
}

export default Fetch;
