import React from "react";

class Input extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            country: '',
        }
    }

    componentDidMount() {
        fetch('https://restcountries.com/v3.1/name/andorra')
                    .then(res => {
                        if (!res.ok) {
                            throw Error('error');
                        }
                        return res.json()
                    })
                    .then((data) => {
                        this.setState({
                            country: {
                                name: data[0].name.common,
                                flag: data[0].flags.png,
                                capital: data[0].capital[0],
                            },
                        })
                    })
                    .catch(e => e)
    }

    render() {
        const   {name, flag, capital} = this.state.country;
        return(
            <div>
                <img src={flag} alt="flag" />
                <p>country's name: {name}</p>
                <p>country's capital: {capital}</p>
            </div>
        )
    }
}

export default Input;
