import React from "react";

class Fetch extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            comment: 0,
        }
    }

    componentDidMount() {
        fetch(' https://jsonplaceholder.typicode.com/comments')
                    .then(res => {
                        if (!res.ok) {
                            throw Error('000');
                        }
                        return res.json()
                    })
                    .then((data) => {
                        this.setState({
                            comment: data[0].body,
                        })
                    })
                    .catch(e => e)
    }

    render() {
        const   element = this.state.comment ? <Layout
                                                comment={this.state.comment}
                                                />
                                            : null;
        
        return(element)
    }
}

function Layout({comment}) {
    return(
        <p>{comment}</p>
    )
}

export default Fetch;
