import React from "react";

class Input extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: 'peru',
        };
    }

    componentDidUpdate() {
        fetch(`https://restcountries.com/v3.1/name/${this.state.name}`)
        .then(res => {
            if (!res.ok) {
                throw Error('error');
            }
            return res.json()
        })
        .then((data) => {
            this.setState({
                name: data[0],
            })
        })
        .catch(e => e)
    }

    render() {
        return(
            <form>
                <input />
                <p>{this.state.name[0].name.common}</p>
            </form>
        )
    }
}

export default Input;
