import React from "react";

class Input extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
        };
    }

    componentDidMount() {
        fetch(`https://restcountries.com/v3.1/name/peru`)
        .then(res => {
            if (!res.ok) {
                throw Error('error');
            }
            return res.json()
        })
        .then((data) => {
            this.setState({
                name: data[0].name.common,
            });
            console.log(this.state.name.common)
        })
        .catch(e => e)
    }

    render() {
        return(
            <form>
                <input />
                <p></p>
            </form>
        )
    }
}

export default Input;
