import React from "react";

class Fetch extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            comment: 0,
        }
    }

    component() {
        fetch(' https://jsonplaceholder.typicode.com/comments')
                    .then(data => data.json())
                    .then((data) => {
                        this.setState({
                            comment: data[0].body,
                        })
                    })
                    .catch(e => e)
    }

    render() {
        // console.log(this.state);
        return(
            <div>
                <button onClick={this.component}>send {this.state.comment}</button>
            </div>
        )
    }
}

export default Fetch;
