import React from "react";

class Input extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            country: '',
        }
    }

    componentDidMount() {
        fetch('https://restcountries.com/v3.1/name/peru')
                    .then(res => {
                        if (!res.ok) {
                            throw Error('error');
                        }
                        return res.json()
                    })
                    .then((data) => {
                        this.setState({
                            country: data[0].name.common,
                        })
                    })
                    .catch(e => e)
    }

    render() {
        return(
            <div>
                <p>comment's id: {this.state.country}</p>
            </div>
        )
    }
}

export default Input;
